<template>
  <div class="share">
    <h5 class="fw-700 mt-5 mb-3">Bagikan</h5>
    <div class="d-flex">
      <div
        v-for="(item, index) in data"
        :key="index"
        class="blog__share mr-2"
        :style="`background-image: url('${item.svg}')`"
        :class="`${item.name}`"
        @click="share(item.name)"
      ></div>
    </div>
  </div>
</template>

<script>
export default {
  props: {
    title: { type: String, required: true }
  },
  methods: {
    share (type) {
      let url = "";
      if (type === "facebook") {
        url = `https://www.facebook.com/sharer/sharer.php?u=${
          window.location.href
          }`;
      }
      if (type === "whatsapp") {
        url = `https://api.whatsapp.com/send?text=${
          this.title
          } | Muslim Dress Taneem ${window.location.href}`;
      }
      if (type === "twitter") {
        url = `https://twitter.com/intent/tweet?text=${
          this.title
          } by Muslim Dress Taneem ${window.location.href}`;
      }
      if (type === "line") {
        url = `https://social-plugins.line.me/lineit/share?url=${
          window.location.href
          }&text=${this.title} | Muslim Dress Taneem `;
      }
      window.open(url, "CNN_WindowName", "width=320,height=400");
    }
  },
  data () {
    return {
      data: [
        {
          name: "facebook",
          svg:
            "data:image/svg+xml;base64,"
        },
        {
          name: "whatsapp",
          svg:
            "data:image/svg+xml;base64,"
        },
        {
          name: "twitter",
          svg:
            "data:image/svg+xml;base64,"
        },
        {
          name: "line",
          svg:
            "data:image/svg+xml;base64,"
        }
      ]
    };
  }
};
</script>

<style lang="scss" scoped>
.share h5 {
  font-size: 18px;
  color: $black80;
}
.blog__share {
  background: white;
  width: 36px;
  height: 36px;
  border-radius: 50%;
  background-repeat: no-repeat;
  background-position: center center;
  cursor: pointer;
}

.facebook,
.whatsapp,
.twitter,
.line {
  background-color: $primary50;
}
</style>
